// time limit
const numWays = (s) => {
    let mod = 10 ** 9 + 7;
    let n = s.length;
    let cnt = 0;
    for (let i = 1; i < n; i++) {
        let s1 = s.slice(0, i);
        let times = count(s1);
        for (let j = n - 1; j > i; j--) {
            let s2 = s.slice(i, j);
            if (count(s2) != times) continue;
            let s3 = s.slice(j, n);
            if (count(s3) != times) continue;
            cnt++;
        }
    }
    return cnt % mod;
};

