const findLength = (A, B) => {
    return LCSubstr(A, B);
};

// https://www.techiedelight.com/longest-common-substring-problem/
// https://www.geeksforgeeks.org/longest-common-substring-dp-29/
const LCSubstr = (a, b) => {
    let m = a.length;
    let n = b.length;
    let dp = initialize2DArrayNew(m + 1, n + 1);
    let max = 0;
    for (let i = 1; i <= m; i++) {
        for (let j = 1; j <= n; j++) {
            if (a[i - 1] == b[j - 1]) {
                dp[i][j] = dp[i - 1][j - 1] + 1;
                max = Math.max(max, dp[i][j]);
            }
        }
    }
    return max;
};

const initialize2DArrayNew = (m, n) => {
    let data = [];
    for (let i = 0; i < m; i++) {
        let tmp = new Array(n).fill(0);
        data.push(tmp);
    }
    return data;
};

