// Accepted --- 276ms 20.00%
const MOD = 1e9 + 7;
const threeSumMulti1 = (A, target) => {
    let m = new Map();
    A.sort((x, y) => x - y);
    let an = A.length;
    for (let i = 0; i < an; i++) {
        if (!m.has(A[i])) m.set(A[i], []);
        m.get(A[i]).push(i);
    }
    // console.log(m);
    let a = Array.from(m.keys());
    let n = a.length;
    let se = new Set();
    for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j++) {
            for (let k = 0; k < n; k++) {
                if (a[i] + a[j] + a[k] == target) {
                    let tmp = [a[i], a[j], a[k]];
                    tmp.sort((a, b) => a - b);
                    let cal = getRecord2(tmp);
                    if (ok(cal, m)) se.add(JSON.stringify([tmp[0], tmp[1], tmp[2]]));
                }
            }
        }
    }
    // console.log(se);
    let res = 0;
    for (const item of se) {
        let a = JSON.parse(item);
        let cal = getRecord2(a);
        let cnt = 1;
        for (const [k, v] of cal) {
            let occ = m.get(k).length;
            // cnt *= occ - v + 1;
            cnt *= combination(occ, v);
        }
        // console.log(cal, cnt)
        res += cnt;
    }
    return res % MOD;
};

// Accepted --- 260ms 20.00%
const threeSumMulti2 = (A, target) => {
    let m = new Map();
    A.sort((x, y) => x - y);
    let an = A.length;
    for (let i = 0; i < an; i++) {
        if (!m.has(A[i])) m.set(A[i], []);
        m.get(A[i]).push(i);
    }
    let a = Array.from(m.keys());
    let n = a.length;
    let se = new Set();
    let res = 0;
    for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j++) {
            for (let k = 0; k < n; k++) {
                if (a[i] + a[j] + a[k] == target) {
                    let tmp = [a[i], a[j], a[k]];
                    tmp.sort((a, b) => a - b);
                    let cal = getRecord2(tmp);
                    if (ok(cal, m)) {
                        let memo = JSON.stringify([tmp[0], tmp[1], tmp[2]]);
                        if (se.has(memo)) continue;
                        let cnt = 1;
                        for (const [k, v] of cal) {
                            let occ = m.get(k).length;
                            cnt *= combination(occ, v);
                        }
                        res += cnt;
                        se.add(memo);
                    }
                }
            }
        }
    }
    return res % MOD;
};

// Accepted --- 256ms 20.00%
const threeSumMulti = (A, target) => {
    let m = getRecord2(A);
    A.sort((x, y) => x - y);
    let a = Array.from(m.keys());
    let n = a.length;
    let se = new Set();
    let res = 0;
    for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j++) {
            for (let k = 0; k < n; k++) {
                if (a[i] + a[j] + a[k] == target) {
                    let tmp = [a[i], a[j], a[k]];
                    tmp.sort((a, b) => a - b);
                    let cal = getRecord2(tmp);
                    if (ok2(cal, m)) {
                        let memo = JSON.stringify([tmp[0], tmp[1], tmp[2]]);
                        if (se.has(memo)) continue;
                        let cnt = 1;
                        for (const [k, v] of cal) {
                            let occ = m.get(k);
                            cnt *= combination(occ, v);
                        }
                        res += cnt;
                        se.add(memo);
                    }
                }
            }
        }
    }
    return res % MOD;
};

const ok = (cal, m) => {
    for (const [k, v] of cal) {
        if (m.get(k).length < v) return false;
    }
    return true;
};

const ok2 = (cal, m) => {
    for (const [k, v] of cal) {
        if (m.get(k) < v) return false;
    }
    return true;
};

const getRecord2 = (arr) => {
    let map = new Map();
    for (const i of arr) {
        map.set(i, (map.get(i) + 1) || 1);
    }
    return map;
};

const combination = (m, n) => {
    return factorial(m, n) / factorial(n, n);
};

const factorial = (m, n) => {
    let num = 1;
    let count = 0;
    for (let i = m; i > 0; i--) {
        if (count == n) break;
        num = num * i;
        count++;
    }
    return num;
};

